import React, { useEffect, useMemo, useState } from "react";

/** LocalStorage keys */
const LS_KEY_DATA = "gtxd_textures_data";
const FAV_KEY = "gtxd_fav_folders";

/** Utils */
function paginate(arr, pageSize, page){const s=(page-1)*pageSize; return arr.slice(s,s+pageSize);}
const norm=(s)=>String(s??"").trim().toLowerCase();
const favKeyOf=(t)=>`${t.modelId}|${t.txdName}|${t.textureName}`;
function parseFavKey(k){const [modelId,txdName,textureName]=k.split("|"); return {modelId:Number(modelId),txdName,textureName};}

/** Simple theme tokens */
const theme={
  app:"bg-neutral-950 text-neutral-100", header:"backdrop-blur bg-neutral-950/70 border-b border-neutral-800",
  input:"bg-neutral-900 border border-neutral-800", button:"bg-neutral-900 border border-neutral-800",
  select:"bg-neutral-900 border border-neutral-800", card:"bg-neutral-900 border border-neutral-800",
  modal:"bg-neutral-950 border border-neutral-800", preview:"bg-neutral-800", muted:"opacity-70",
  navHome:"border-indigo-500/60 bg-indigo-500/10", navFav:"border-pink-500/60 bg-pink-500/10", navTut:"border-emerald-500/60 bg-emerald-500/10",
  success:"bg-emerald-500/20 border-emerald-600 text-emerald-300", error:"bg-rose-500/20 border-rose-600 text-rose-300",
  favActive:"border-pink-500/50 bg-pink-500/20", favIdle:"border-neutral-700 bg-neutral-900/70",
};

/** Crypto helpers (AES-GCM + PBKDF2) */
async function deriveKey(password, salt){
  const enc=new TextEncoder();
  const keyMaterial=await crypto.subtle.importKey("raw", enc.encode(password), "PBKDF2", false, ["deriveKey"]);
  return crypto.subtle.deriveKey(
    {name:"PBKDF2", salt, iterations:150000, hash:"SHA-256"},
    keyMaterial, {name:"AES-GCM", length:256}, false, ["encrypt","decrypt"]
  );
}
function b64u(arr){return btoa(String.fromCharCode(...new Uint8Array(arr))).replace(/\+/g,"-").replace(/\//g,"_").replace(/=+$/,"");}
function ub64u(s){s=s.replace(/-/g,"+").replace(/_/g,"/"); while(s.length%4) s+="="; const bin=atob(s); const out=new Uint8Array(bin.length); for(let i=0;i<bin.length;i++) out[i]=bin.charCodeAt(i); return out.buffer;}

/** Parser table with autodetect (comma/semicolon/tab/pipe/spaceâ‰¥2) */
function detectDelimiter(text){
  const first=(text.split(/\r?\n/)[0]||"").replace(/^\ufeff/,"");
  const counts={",":(first.match(/,/g)||[]).length,";":(first.match(/;/g)||[]).length,"\t":(first.match(/\t/g)||[]).length,"|":(first.match(/\|/g)||[]).length};
  const top=Object.entries(counts).sort((a,b)=>b[1]-a[1])[0];
  if(!top||top[1]===0){ if(/\S(?:  +)\S/.test(first)) return / {2,}/; return ","; }
  return top[0];
}
function parseTable(text){
  const delim=detectDelimiter(text);
  const lines=text.split(/\r?\n/).filter(l=>l.trim().length);
  if(!lines.length) return [];
  if(delim instanceof RegExp){
    return lines.map(line=>line.replace(/^\ufeff/,"").trim().split(delim).map(c=>c.trim()));
  }
  const rows=[];
  for(const raw of lines){
    const line=String(raw).replace(/^\ufeff/,"");
    const row=[]; let cell=""; let inQ=false;
    for(let i=0;i<line.length;i++){
      const ch=line[i], next=line[i+1];
      if(inQ){ if(ch=='"'&&next=='"'){cell+='"'; i++; continue;} if(ch=='"'){inQ=false; continue;} cell+=ch; }
      else { if(ch=='"'){inQ=true; continue;} if(ch===delim){row.push(cell.trim()); cell=""; continue;} cell+=ch; }
    }
    row.push(cell.trim()); rows.push(row);
  }
  return rows;
}

export default function App(){
  /** App state */
  const [dataset,setDataset]=useState(()=>{try{const raw=localStorage.getItem(LS_KEY_DATA);const p=raw?JSON.parse(raw):[];return Array.isArray(p)?p:[]}catch{return[]}});
  const [fav, setFav]=useState(()=>{try{const raw=localStorage.getItem(FAV_KEY); return raw?JSON.parse(raw):{folders:{Unfiled:[]}, lastFolder:"Unfiled"};}catch{return{folders:{Unfiled:[]}, lastFolder:"Unfiled"}}});
  useEffect(()=>{try{localStorage.setItem(LS_KEY_DATA,JSON.stringify(dataset));}catch{}},[dataset]);
  useEffect(()=>{try{localStorage.setItem(FAV_KEY,JSON.stringify(fav));}catch{}},[fav]);

  const [view,setView]=useState("home"); // home | favorites | tutorial
  const [query,setQuery]=useState("");
  const [pageSize,setPageSize]=useState(20);
  const [page,setPage]=useState(1);
  const [hideDupes,setHideDupes]=useState(false);
  const [active,setActive]=useState(null);
  const [toast,setToast]=useState(null);
  const [busy,setBusy]=useState(false);

  // Favorites UI
  const folderNames=Object.keys(fav.folders||{});
  const [favViewFolder,setFavViewFolder]=useState(()=>folderNames[0]||"Unfiled");
  const [manageOpen, setManageOpen]=useState(false);
  const [confirmDel, setConfirmDel]=useState({open:false, target:null}); // delete single favorite item modal

  function showToast(kind,msg){ setToast({type:kind,msg}); clearTimeout(showToast._t); showToast._t=setTimeout(()=>setToast(null),3000); }

  /** Base / derived lists */
  const baseList=useMemo(()=>{
    if(view!=="favorites") return dataset;
    const keys=new Set(fav.folders[favViewFolder]||[]);
    return dataset.filter(t=>keys.has(favKeyOf(t)));
  },[view,dataset,fav,favViewFolder]);
  const filtered=useMemo(()=>{
    const q=norm(query);
    let data=baseList.filter(t=>[t.textureName,t.txdName,t.modelId,t.libraryName].map(String).some(v=>norm(v).includes(q)));
    if(hideDupes) data=data.filter(t=>!t.duplicateOf);
    return data;
  },[baseList,query,hideDupes]);
  const total=filtered.length;
  const maxPage=Math.max(1,Math.ceil(total/pageSize));
  const pageSafe=Math.min(page,maxPage);
  const items=paginate(filtered,pageSize,pageSafe);

  /** Favorites helpers */
  function isFavAnywhere(t){const k=favKeyOf(t); return Object.values(fav.folders).some(l=>l.includes(k));}
  function addFavToFolder(t, folder){
    const k=favKeyOf(t);
    setFav(prev=>{
      const folders={...prev.folders};
      const set=new Set(folders[folder]||[]); set.add(k);
      folders[folder]=Array.from(set);
      return {folders, lastFolder: folder};
    });
  }
  function removeFavFromAllFolders(t){
    const k=favKeyOf(t);
    setFav(prev=>{
      const folders=Object.fromEntries(Object.entries(prev.folders).map(([f,arr])=>[f,arr.filter(x=>x!==k)]));
      return {folders, lastFolder: prev.lastFolder};
    });
  }

  /** Import base textures */
  async function handleImport(ev){
    const file=ev.target.files?.[0]; if(!file) return;
    setBusy(true);
    try{
      const text=await file.text();
      const rows=parseTable(text);
      if(!rows.length) throw new Error("File kosong");
      const header=rows[0].map(s=>String(s).trim().toLowerCase());
      const dataRows=rows.slice(1);
      const col=(names)=>names.map(n=>String(n).toLowerCase()).map(n=>header.indexOf(n)).find(i=>i>=0);
      const idxModel=col(["modelid","model_id","model","id","model id","Model ID"]);
      const idxTXD=col(["txdname","txd","txd_name","txd name","TXD Name"]);
      const idxName=col(["texturename","texture","name","texture name","Texture Name"]);
      const idxURL=col(["url","image","img","URL"]);
      if(idxModel==null||idxTXD==null||idxName==null||idxURL==null) throw new Error("Header wajib: modelid, txdname, texturename, url");
      const next=[]; let autoId=1;
      for(const r of dataRows){
        if(r.every(c=>String(c).trim()==="")) continue;
        const modelIdRaw=r[idxModel];
        const modelId=(modelIdRaw===undefined||modelIdRaw===null||String(modelIdRaw).trim()==="")?null:Number(modelIdRaw);
        const txdName=String(r[idxTXD]??"").trim();
        const textureName=String(r[idxName]??"").trim();
        let url=String(r[idxURL]??"").trim();
        if(!modelId||!txdName||!textureName) continue;
        if(!url) url=`https://gtxd.net/images/gtasa_textures/${txdName}.${textureName}.png`;
        next.push({id:autoId++, textureName, modelId, txdName, libraryName:"gta3.img", duplicateOf:null, urlEnc:btoa(url)}); // store obfuscated
      }
      if(!next.length) throw new Error("Tidak ada baris valid");
      setDataset(next); setView("home"); setQuery(""); setHideDupes(false); setPage(1);
      showToast("success",`Berhasil memuat ${next.length} data.`);
    }catch(e){
      console.error(e);
      showToast("error",`Gagal import: ${e.message||e}`);
    }finally{
      setBusy(false);
      ev.target.value=""; // reset so user can retry same file
    }
  }

  /** Export / Import Favorites (encrypted) with modal prompts */
  const [encModal, setEncModal]=useState({open:false, mode:null}); // mode: 'export'|'import'
  const [encPass, setEncPass]=useState("");
  const [encFile, setEncFile]=useState(null);

  async function doExportEncrypted(){
    try{
      const payload={};
      for(const [folder, list] of Object.entries(fav.folders)){
        payload[folder]=list.map(k=>{
          const {modelId,txdName,textureName}=parseFavKey(k);
          const item=dataset.find(d=>d.modelId===modelId && d.txdName===txdName && d.textureName===textureName);
          const url=item?atob(item.urlEnc):`https://gtxd.net/images/gtasa_textures/${txdName}.${textureName}.png`;
          return {modelid:modelId, txdname:txdName, texturename:textureName, url};
        });
      }
      // optional attached file contents
      let attachment=null;
      if(encFile){ attachment=await encFile.arrayBuffer(); }

      const salt=crypto.getRandomValues(new Uint8Array(16));
      const iv=crypto.getRandomValues(new Uint8Array(12));
      const key=await deriveKey(encPass, salt);
      const enc=await crypto.subtle.encrypt({name:"AES-GCM", iv}, key, new TextEncoder().encode(JSON.stringify({payload, attachment: attachment?b64u(attachment):null})));
      const out={v:1, a:"AES-GCM", kdf:"PBKDF2-SHA256-150k", salt:b64u(salt), iv:b64u(iv), data:b64u(enc)};
      const blob=new Blob([JSON.stringify(out,null,2)],{type:"application/json"});
      const url=URL.createObjectURL(blob); const a=document.createElement("a");
      a.href=url; a.download=`favorites_encrypted_${new Date().toISOString().slice(0,10)}.tfx.json`; document.body.appendChild(a); a.click(); a.remove(); URL.revokeObjectURL(url);
      showToast("success","Favorites diexport terenkripsi.");
    }catch(e){
      console.error(e); showToast("error",`Gagal export: ${e.message||e}`);
    }finally{
      setEncModal({open:false, mode:null}); setEncPass(""); setEncFile(null);
    }
  }

  async function doImportEncrypted(file){
    try{
      const text=await file.text();
      const obj=JSON.parse(text);
      if(!obj||!obj.data||!obj.salt||!obj.iv) throw new Error("File tidak valid");
      const salt=ub64u(obj.salt); const iv=ub64u(obj.iv); const key=await deriveKey(encPass, new Uint8Array(salt));
      const dec=await crypto.subtle.decrypt({name:"AES-GCM", iv:new Uint8Array(iv)}, key, ub64u(obj.data));
      const parsed=JSON.parse(new TextDecoder().decode(dec));
      const folders={...fav.folders}; let added=0;
      for(const [folder, arr] of Object.entries(parsed.payload||{})){
        if(!folders[folder]) folders[folder]=[];
        const set=new Set(folders[folder]);
        for(const it of (arr||[])){ const k=`${it.modelid}|${it.txdname}|${it.texturename}`; if(!set.has(k)){ set.add(k); added++; } }
        folders[folder]=Array.from(set);
      }
      setFav({folders, lastFolder:fav.lastFolder});
      showToast("success",`Favorites loaded: ${added} entri baru.`);
    }catch(e){ console.error(e); showToast("error",`Gagal import: ${e.message||e}`); }
    finally { setEncModal({open:false, mode:null}); setEncPass(""); }
  }

  /** Manage folders modal */
  const [manageData, setManageData]=useState({open:false});
  function openManage(){ setManageOpen(true); setManageData({open:true}); }
  function closeManage(){ setManageOpen(false); setManageData({open:false}); }

  function createFolder(name){
    name=String(name||"").trim(); if(!name) return;
    if(fav.folders[name]){ showToast("error","Folder sudah ada."); return; }
    setFav(prev=>({folders:{...prev.folders,[name]:[]}, lastFolder:name}));
    setFavViewFolder(name);
    showToast("success",`Folder "${name}" dibuat.`);
  }
  function renameFolder(oldName, newName){
    newName=String(newName||"").trim(); if(!newName) return;
    if(!fav.folders[oldName]) return;
    if(fav.folders[newName]){ showToast("error","Nama baru sudah ada."); return; }
    setFav(prev=>{
      const folders={...prev.folders}; const items=folders[oldName]||[]; delete folders[oldName]; folders[newName]=items;
      return {folders, lastFolder: prev.lastFolder===oldName?newName:prev.lastFolder};
    });
    if(favViewFolder===oldName) setFavViewFolder(newName);
  }
  function deleteFolder(name, moveTo){
    if(!fav.folders[name]) return;
    setFav(prev=>{
      const folders={...prev.folders}; const items=folders[name]||[]; delete folders[name];
      if(moveTo && folders[moveTo]){
        const set=new Set([...(folders[moveTo]||[]), ...items]); folders[moveTo]=Array.from(set);
      }
      const newLast=prev.lastFolder===name ? Object.keys(folders)[0]||"Unfiled" : prev.lastFolder;
      return {folders, lastFolder:newLast};
    });
    if(favViewFolder===name) setFavViewFolder(Object.keys(fav.folders)[0]||"Unfiled");
  }
  function moveAll(from, to){
    if(!fav.folders[from]||!fav.folders[to]) return;
    setFav(prev=>{
      const folders={...prev.folders};
      const set=new Set([...(folders[to]||[]), ...(folders[from]||[])]);
      folders[to]=Array.from(set); folders[from]=[];
      return {folders, lastFolder: prev.lastFolder};
    });
  }

  /** UI */
  const favCount=(fav.folders[favViewFolder]||[]).length;

  return (
    <div className={`min-h-screen ${theme.app}`}>
      {/* Toast */}
      {toast && (<div className={`fixed top-4 right-4 z-50 px-4 py-2 rounded-xl border shadow ${toast.type==='success'?theme.success:theme.error}`}>{toast.msg}</div>)}

      <header className={`sticky top-0 z-20 ${theme.header}`}>
        <div className="max-w-6xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="text-2xl font-black tracking-tight">TexFind</div>
          <nav className="flex items-center gap-2">
            <button onClick={()=>setView('home')} className={`px-3 py-1 rounded-xl border text-sm ${view==='home'?theme.navHome:theme.button}`}>Home</button>
            <button onClick={()=>setView('favorites')} className={`px-3 py-1 rounded-xl border text-sm ${view==='favorites'?theme.navFav:theme.button}`}>Favorites</button>
            <button onClick={()=>setView('tutorial')} className={`px-3 py-1 rounded-xl border text-sm ${view==='tutorial'?theme.navTut:theme.button}`}>Tutorial</button>
          </nav>
        </div>
      </header>

      <main className="max-w-6xl mx-auto px-4 py-6">
        {/* Toolbar - only show import/clear on Home */}
        <div className="mb-4 flex flex-wrap items-center gap-2">
          {view==='home' && (
            <>
              <label className={`px-3 py-2 rounded-xl cursor-pointer ${theme.button}`}>
                <input type="file" accept=".csv,.tsv,.txt,text/csv,text/tab-separated-values,text/plain" className="hidden" onChange={handleImport} />
                Import Textures (CSV/TSV/TXT)
              </label>
              <button onClick={()=>{ setDataset([]); try{localStorage.removeItem(LS_KEY_DATA);}catch{} setPage(1); showToast("success","Dataset dikosongkan."); }} className={`px-3 py-2 rounded-xl ${theme.button}`}>Clear data</button>
            </>
          )}

          {view==='favorites' && (
            <>
              <span className="text-sm">Folder</span>
              <select className={`px-3 py-2 rounded-xl ${theme.select}`} value={favViewFolder} onChange={(e)=>setFavViewFolder(e.target.value)}>
                {folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}
                {!folderNames.includes("Unfiled") && <option value="Unfiled">Unfiled</option>}
              </select>
              <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={()=>openManage()}>Manage</button>

              {/* Right side export/import */}
              <div className="ml-auto flex gap-2">
                <button onClick={()=>setEncModal({open:true, mode:'export'})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Export Favorites</button>
                <button onClick={()=>setEncModal({open:true, mode:'import'})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Import Favorites</button>
              </div>
            </>
          )}

          <span className={`ml-auto text-xs ${theme.muted}`}>Data source: {dataset.length?"imported":"empty"} â€¢ {dataset.length} rows</span>
        </div>

        {/* Filters (after dataset loaded) */}
        {dataset.length>0 && view!=='tutorial' && (
          <div className="mb-4 flex flex-wrap items-center gap-3">
            <input value={query} onChange={(e)=>{setPage(1); setQuery(e.target.value);}} placeholder={view==='favorites'?"Search favorites...":"Search textures..."} className={`w-full md:w-72 px-3 py-2 rounded-xl outline-none focus:ring-2 focus:ring-indigo-500 ${theme.input}`} />
            <div className="flex items-center gap-2">
              <label className={`text-sm ${theme.muted}`}>Show</label>
              <select value={pageSize} onChange={(e)=>{setPage(1); setPageSize(parseInt(e.target.value,10));}} className={`px-3 py-2 rounded-xl ${theme.select}`}>
                {[20,40,60,80,100].map(n=><option key={n} value={n}>{n}</option>)}
              </select>
              <span className={`text-sm ${theme.muted}`}>entries</span>
            </div>
            <label className="inline-flex items-center gap-2 text-sm">
              <input type="checkbox" checked={hideDupes} onChange={(e)=>{setPage(1); setHideDupes(e.target.checked);}} /> Hide duplicates
            </label>
            <div className={`ml-auto text-sm ${theme.muted}`}>{total.toLocaleString()} item ditemukan</div>
          </div>
        )}

        {/* Tutorial Page */}
        {view==='tutorial' && (
          <div className={`rounded-2xl p-5 ${theme.card} space-y-4`}>
            <h2 className="text-xl font-bold">Panduan Builder JGRP: Menggunakan TexFind</h2>
            <ol className="list-decimal pl-6 space-y-2 text-sm leading-relaxed">
              <li>Import <b>base textures</b> (CSV/TSV/TXT) di tab <b>Home</b>. Wajib kolom: <code>modelid, txdname, texturename, url</code>. URL boleh kosongâ€”akan otomatis diisi.</li>
              <li>Gunakan search/filter untuk menemukan texture. Buka kartu untuk melihat detail dan <b>Builder Code</b> dalam format: <code>[model id] [txd name] [texture name]</code>.</li>
              <li>Simpan ke <b>Favorites</b> (pakai icon hati). Pilih folder tujuan di modal, agar koleksi rapi.</li>
              <li>Di JGRP, untuk menerapkan texture dengan builder kode, gunakan perintah <code>/dyoh</code> sesuai prosedur mapping. Masukkan nilai: <b>Model ID</b>, <b>TXD Name</b>, <b>Texture Name</b> dari Builder Code. Pastikan objek target benar.</li>
              <li>Gunakan <b>Export Favorites</b> (terenkripsi) untuk backup dan <b>Import Favorites</b> saat memulai sesi baru.</li>
            </ol>
            <div className={`text-xs ${theme.muted}`}>Catatan: ikuti regulasi builder JGRP dan lakukan preview terlebih dahulu sebelum commit perubahan di peta.</div>
          </div>
        )}

        {/* Empty / Grid */}
        {view!=='tutorial' && (items.length===0 ? (
          <div className={`rounded-2xl p-8 text-center ${theme.card}`}>
            <div className="text-sm opacity-80 mb-2">{dataset.length===0? "Belum ada data. Import di tab Home.":"Tidak ada hasil yang cocok."}</div>
            <div className="text-xs opacity-60">Coba hapus filter/pencarian.</div>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
            {items.map(t=>(
              <div key={t.id} className="relative">
                <button onClick={()=>setActive(t)} className={`w-full text-left rounded-2xl p-4 hover:border-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 ${theme.card}`}>
                  <div className="text-sm font-semibold truncate pr-8">{t.textureName}</div>
                  <div className={`mt-2 h-28 w-full rounded-xl flex items-center justify-center overflow-hidden ${theme.preview}`}>
                    <img src={atob(t.urlEnc)} alt={t.textureName} onError={(e)=>{e.currentTarget.style.display='none'; e.currentTarget.parentElement.innerText='No preview';}} className="w-full h-full object-cover"/>
                  </div>
                  <div className={`mt-2 text-xs ${theme.muted}`}>More info</div>
                </button>
                <button
                  className={`absolute top-2 right-2 rounded-full border px-2 py-1 text-xs backdrop-blur transition ${isFavAnywhere(t)?theme.favActive:theme.favIdle}`}
                  onClick={(e)=>{e.stopPropagation(); if(isFavAnywhere(t)){ setConfirmDel({open:true, target:t}); } else { addFavToFolder(t, fav.lastFolder || "Unfiled"); showToast("success","Ditambahkan ke favorites."); } }}
                  title={isFavAnywhere(t)?"Remove from favorites":"Add to favorites"}
                >{isFavAnywhere(t)?"â˜…":"â˜†"}</button>
              </div>
            ))}
          </div>
        ))}

        {/* Pagination */}
        {view!=='tutorial' && (
          <div className="mt-6 flex items-center justify-center gap-2">
            <button onClick={()=>setPage(p=>Math.max(1,p-1))} className={`px-3 py-2 rounded-xl disabled:opacity-40 ${theme.button}`} disabled={pageSafe===1}>Prev</button>
            <div className={`text-sm ${theme.muted}`}>Page {pageSafe} / {maxPage}</div>
            <button onClick={()=>setPage(p=>Math.min(maxPage,p+1))} className={`px-3 py-2 rounded-xl disabled:opacity-40 ${theme.button}`} disabled={pageSafe===maxPage}>Next</button>
          </div>
        )}
      </main>

      {/* Details Modal (URL hidden/obfuscated) */}
      {active && (
        <div className="fixed inset-0 z-30 bg-black/70 backdrop-blur-sm flex items-center justify-center p-4" onClick={()=>setActive(null)}>
          <div className={`w-full max-w-2xl rounded-2xl ${theme.modal}`} onClick={(e)=>e.stopPropagation()}>
            <div className="p-4 border-b border-inherit flex items-start justify-between">
              <div>
                <div className="text-lg font-bold">{active.textureName}</div>
                <div className={`text-xs ${theme.muted}`}>Texture details</div>
              </div>
              <button onClick={()=>setActive(null)} className={`px-3 py-1 rounded-xl ${theme.button}`}>Close</button>
            </div>
            <div className="p-4 grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-3">
                <div className={`h-40 rounded-xl flex items-center justify-center overflow-hidden ${theme.preview}`}>
                  <img src={atob(active.urlEnc)} alt={active.textureName} onError={(e)=>{e.currentTarget.style.display='none'; e.currentTarget.parentElement.innerText='No preview';}} className="w-full h-full object-cover"/>
                </div>
                {/* URL intentionally hidden to reduce scraping */}
              </div>
              <div className="space-y-2 text-sm">
                <Row label="Model ID" value={active.modelId}/>
                <Row label="TXD Name" value={active.txdName}/>
                <Row label="Texture Name" value={active.textureName}/>
                {/* Builder code */}
                <div className={`rounded-xl p-3 ${theme.card}`}>
                  <div className="text-xs opacity-70 mb-1">Builder Code</div>
                  <Copy code={`${active.modelId} ${active.txdName} ${active.textureName}`} />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Delete Favorite confirm modal */}
      {confirmDel.open && (
        <div className="fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4" onClick={()=>setConfirmDel({open:false, target:null})}>
          <div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={(e)=>e.stopPropagation()}>
            <div className="text-lg font-bold">Hapus dari Favorites?</div>
            <div className="text-sm mt-1">Item akan dihapus dari semua folder favorit.</div>
            <div className="mt-4 flex gap-2 justify-end">
              <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={()=>setConfirmDel({open:false, target:null})}>Batal</button>
              <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={()=>{ removeFavFromAllFolders(confirmDel.target); setConfirmDel({open:false, target:null}); showToast("success","Dihapus dari favorites."); }}>Hapus</button>
            </div>
          </div>
        </div>
      )}

      {/* Export/Import Encrypted Modal */}
      {encModal.open && (
        <div className="fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4" onClick={()=>{setEncModal({open:false, mode:null}); setEncPass(""); setEncFile(null);}}>
          <div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={(e)=>e.stopPropagation()}>
            <div className="text-lg font-bold mb-2">{encModal.mode==='export' ? "Export Favorites (Encrypted)" : "Import Favorites (Encrypted)"}</div>
            <div className="space-y-3">
              <div>
                <div className="text-sm opacity-80 mb-1">Kunci (password)</div>
                <input type="password" className={`w-full px-3 py-2 rounded-xl ${theme.input}`} value={encPass} onChange={(e)=>setEncPass(e.target.value)} placeholder="Masukkan kunci Anda"/>
                <div className={`text-[10px] mt-1 ${theme.muted}`}>Jangan lupa kunci Anda. Tanpa kunci, data tidak bisa dipulihkan.</div>
              </div>
              {encModal.mode==='export' && (
                <div>
                  <div className="text-sm opacity-80 mb-1">Lampirkan file (opsional)</div>
                  <input type="file" onChange={(e)=>setEncFile(e.target.files?.[0]||null)} />
                </div>
              )}
              {encModal.mode==='import' && (
                <div>
                  <div className="text-sm opacity-80 mb-1">Pilih file favorites (.tfx.json)</div>
                  <input type="file" accept="application/json,.json" onChange={(e)=>{ const f=e.target.files?.[0]; if(f) doImportEncrypted(f); e.target.value=""; }} />
                </div>
              )}
            </div>
            <div className="mt-4 flex gap-2 justify-end">
              <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={()=>{setEncModal({open:false, mode:null}); setEncPass(""); setEncFile(null);}}>Batal</button>
              {encModal.mode==='export' && <button disabled={!encPass} className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={doExportEncrypted}>Export</button>}
            </div>
          </div>
        </div>
      )}

      {/* Manage Folders Modal */}
      {manageOpen && (
        <div className="fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4" onClick={closeManage}>
          <div className={`w-full max-w-2xl rounded-2xl ${theme.modal} p-4`} onClick={(e)=>e.stopPropagation()}>
            <div className="flex items-center justify-between mb-3">
              <div className="text-lg font-bold">Manage Folders</div>
              <div className="flex gap-2">
                <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={()=>{
                  const name=prompt("Nama folder baru:"); if(!name) return; createFolder(name);
                }}>+ Folder</button>
                <button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={closeManage}>Close</button>
              </div>
            </div>
            <div className="space-y-2 max-h-[60vh] overflow-auto">
              {Object.entries(fav.folders).map(([name, list])=> (
                <div key={name} className={`p-3 rounded-xl flex items-center justify-between ${theme.card}`}>
                  <div className="flex items-center gap-3">
                    <div className="font-semibold">{name}</div>
                    <div className={`text-xs ${theme.muted}`}>{list.length} items</div>
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <button className={`px-3 py-1 rounded-xl ${theme.button}`} onClick={()=>{
                      const nn=prompt(`Rename "${name}" ke:`, name); if(!nn || nn===name) return; renameFolder(name, nn);
                    }}>Rename</button>
                    <button className={`px-3 py-1 rounded-xl ${theme.button}`} onClick={()=>{
                      const candidates=Object.keys(fav.folders).filter(f=>f!==name);
                      const to=candidates.length ? prompt(`Hapus folder "${name}"? Tulis nama folder tujuan untuk memindahkan ${fav.folders[name].length} item, atau kosongkan untuk buang:`, candidates[0]) : "";
                      deleteFolder(name, to && fav.folders[to]!==undefined ? to : undefined);
                    }}>Delete</button>
                    {Object.keys(fav.folders).length>1 && (
                      <button className={`px-3 py-1 rounded-xl ${theme.button}`} onClick={()=>{
                        const to=prompt(`Pindahkan semua item dari "${name}" ke folder mana?`, Object.keys(fav.folders).find(f=>f!==name)||"");
                        if(to && fav.folders[to]!==undefined) moveAll(name,to);
                      }}>Move all</button>
                    )}
                  </div>
                </div>
              ))}
              {Object.keys(fav.folders).length===0 && (
                <div className={`p-3 rounded-xl ${theme.card}`}>Belum ada folder.</div>
              )}
            </div>
          </div>
        </div>
      )}

      <footer className="max-w-6xl mx-auto px-4 py-10 opacity-70 text-xs">
        <div>TexFind â€¢ rezam5n / dvberg â€¢ Â© 2025</div>
      </footer>
    </div>
  );
}

function Row({label,value}){
  return (<div className="flex items-center gap-2">
    <div className="w-28 shrink-0 opacity-70">{label}</div>
    <div className="truncate">{String(value??"")}</div>
  </div>);
}

function Copy({code}){
  const [copied,setCopied]=useState(false);
  return (
    <div className={`rounded-xl overflow-hidden border ${theme.card}`}>
      <div className="px-3 py-2 text-[10px] opacity-70 flex items-center justify-between border-b border-inherit">
        <span>Kode</span>
        <button className={`px-2 py-0.5 rounded-lg border`} onClick={()=>{navigator.clipboard.writeText(code); setCopied(true); setTimeout(()=>setCopied(false),1000);}}>{copied?"Copied":"Copy"}</button>
      </div>
      <pre className="p-3 text-xs overflow-auto">{code}</pre>
    </div>
  );
}
