import React, { useEffect, useMemo, useRef, useState } from "react";
const LS_KEY_DATA="gtxd_textures_data"; const FAV_KEY="gtxd_fav_folders";
function paginate(a,s,p){const st=(p-1)*s;return a.slice(st,st+s);} const norm=s=>String(s??"").trim().toLowerCase();
const favKeyOf=t=>`${t.modelId}|${t.txdName}|${t.textureName}`;
const encUrl=u=>btoa(unescape(encodeURIComponent(String(u)))); const decUrl=e=>decodeURIComponent(escape(atob(String(e||""))));
const theme={app:"bg-neutral-950 text-neutral-100",header:"backdrop-blur bg-neutral-950/70 border-b border-neutral-800",input:"bg-neutral-900 border border-neutral-800",button:"bg-neutral-900 border border-neutral-800",select:"bg-neutral-900 border border-neutral-800",chip:"bg-neutral-800 text-neutral-300",card:"bg-neutral-900 border border-neutral-800",modal:"bg-neutral-950 border border-neutral-800",preview:"bg-neutral-800",favActive:"border-pink-500/50 bg-pink-500/20",favIdle:"border-neutral-700 bg-neutral-900/70",navHome:"border-indigo-500/60 bg-indigo-500/10",navFav:"border-pink-500/60 bg-pink-500/10",navTut:"border-emerald-500/60 bg-emerald-500/10",muted:"opacity-70",success:"bg-emerald-500/20 border-emerald-600 text-emerald-300",error:"bg-rose-500/20 border-rose-600 text-rose-300"};
async function deriveKey(pw,salt){const enc=new TextEncoder();const mat=await crypto.subtle.importKey("raw",enc.encode(pw),"PBKDF2",false,["deriveKey"]);return crypto.subtle.deriveKey({name:"PBKDF2",hash:"SHA-256",salt,iterations:150000},mat,{name:"AES-GCM",length:256},false,["encrypt","decrypt"]);}
const toB64=b=>btoa(String.fromCharCode(...new Uint8Array(b))); const fromB64=b=>Uint8Array.from(atob(b),c=>c.charCodeAt(0));
async function encryptJSON(obj,pw,att){const salt=crypto.getRandomValues(new Uint8Array(16)); const iv=crypto.getRandomValues(new Uint8Array(12)); const key=await deriveKey(pw,salt); const enc=new TextEncoder(); const payload={data:obj,attachment:att||null}; const ct=await crypto.subtle.encrypt({name:"AES-GCM",iv},key,enc.encode(JSON.stringify(payload))); return {v:1,alg:"AES-GCM-256/PBKDF2-150k",iv:toB64(iv),salt:toB64(salt),ct:toB64(ct)};}
async function decryptJSON(p,pw){const salt=fromB64(p.salt),iv=fromB64(p.iv),ct=fromB64(p.ct); const key=await deriveKey(pw,salt); const pt=await crypto.subtle.decrypt({name:"AES-GCM",iv},key,ct); return JSON.parse(new TextDecoder().decode(pt));}
function parseTable(text){const first=(text.split(/\r?\n/)[0]||'').replace(/^\ufeff/,'');const counts={',':(first.match(/,/g)||[]).length,';':(first.match(/;/g)||[]).length,'\t':(first.match(/\t/g)||[]).length,'|':(first.match(/\|/g)||[]).length};let delim=Object.entries(counts).sort((a,b)=>b[1]-a[1])[0];let useSp=/\S(?:  +)\S/.test(first);const lines=text.split(/\r?\n/).filter(l=>l.trim().length);if(!lines.length)return[];if(!delim||delim[1]===0){if(useSp){return lines.map(l=>l.replace(/^\ufeff/,'').trim().split(/ {2,}/).map(c=>c.trim()));}delim=[',',0];}
const d=delim[0]==='\t'?'\t':delim[0];const rows=[];for(const raw of lines){const line=String(raw).replace(/^\ufeff/,'');const row=[];let cell='',inQ=false;for(let i=0;i<line.length;i++){const ch=line[i],nx=line[i+1];if(inQ){if(ch==='"'&&nx==='"'){cell+='"';i++;continue;}if(ch==='"'){inQ=false;continue;}cell+=ch;}else{if(ch==='"'){inQ=true;continue;}if(d==='\t'?ch==='\t':ch===d){row.push(cell.trim());cell='';continue;}cell+=ch;}}row.push(cell.trim());rows.push(row);}return rows;}
function Row({label,value}){return (<div className='flex items-center gap-2'><div className='w-28 shrink-0 opacity-70'>{label}</div><div className='truncate'>{String(value??'')}</div></div>);} 
function Code({label,code}){const [copied,setCopied]=useState(false);return (<div className={`rounded-2xl overflow-hidden border ${theme.card}`}><div className='px-3 py-2 text-[10px] opacity-70 flex items-center justify-between border-b border-inherit'><span>{label}</span><button className='px-2 py-0.5 rounded-lg border' onClick={()=>{navigator.clipboard.writeText(code);setCopied(true);setTimeout(()=>setCopied(false),1000);}}>{copied?'Copied':'Copy'}</button></div><pre className='p-3 text-xs overflow-auto'>{code}</pre></div>);} 
function HeartIcon({filled}){return (<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' width='16' height='16' fill={filled?'currentColor':'none'} className='inline'><path d='M12 21s-6.716-4.297-9.193-7.243C-0.298 12.21.16 8.53 2.98 7.157A4.93 4.93 0 0 1 12 8.09a4.93 4.93 0 0 1 9.02-0.933c2.82 1.372 3.279 5.052-.827 6.6C18.716 16.703 12 21 12 21z' stroke='currentColor' strokeWidth='1.5'/></svg>);} 
function Tutorial(){return (<div className={`rounded-2xl p-5 ${theme.card}`}><h2 className='text-xl font-bold mb-2'>Tutorial Builder SAMP di JGRP</h2><ol className='list-decimal ml-6 space-y-2 text-sm'><li>Pastikan akun JGRP kamu sudah terdaftar dan memiliki izin builder (ajukan di forum JGRP).</li><li>Gunakan object editor (Map Construction/MTA) untuk menyusun objek. Catat <b>model ID</b>, <b>TXD Name</b>, <b>Texture Name</b>.</li><li>Gunakan <b>TexFind</b> untuk mencari dan menyusun texture yang sesuai.</li><li>Di server, gunakan perintah mapping seperti:<ul className='list-disc ml-6 mt-1'><li><code>/edittexture &lt;modelId&gt; &lt;txdName&gt; &lt;textureName&gt;</code></li><li><code>/mmat &lt;modelId&gt; &lt;txdName&gt; &lt;textureName&gt;</code></li></ul></li><li>Gunakan 'Builder Code' dari detail untuk menyalin cepat.</li><li>Uji di server lokal dulu sebelum submit ke JGRP.</li><li>Dokumentasikan perubahan saat ajukan ke tim mapping JGRP.</li></ol><h3 className='text-lg font-semibold mt-4'>Tips</h3><ul className='list-disc ml-6 space-y-1 text-sm'><li>Simpan set favorit per-proyek pada folder berbeda.</li><li>Export terenkripsi untuk berbagi aman.</li><li>Cek kembali penulisan <code>txdname.texturename</code> jika gambar tidak muncul.</li></ul></div>);} 
export default function App(){const [dataset,setDataset]=useState(()=>{try{const raw=localStorage.getItem(LS_KEY_DATA);const p=raw?JSON.parse(raw):[];return Array.isArray(p)?p:[];}catch{return[]}}); useEffect(()=>{try{localStorage.setItem(LS_KEY_DATA,JSON.stringify(dataset));}catch{}},[dataset]);
const [favFolders,setFavFolders]=useState(()=>{try{const raw=localStorage.getItem(FAV_KEY);return raw?JSON.parse(raw):{folders:{Unfiled:[]},lastFolder:'Unfiled'};}catch{return{folders:{Unfiled:[]},lastFolder:'Unfiled'}}}); useEffect(()=>{try{localStorage.setItem(FAV_KEY,JSON.stringify(favFolders));}catch{}},[favFolders]);
const [view,setView]=useState('home'); const [query,setQuery]=useState(''); const [pageSize,setPageSize]=useState(20); const [page,setPage]=useState(1); const [hideDupes,setHideDupes]=useState(false); const [active,setActive]=useState(null); const [toast,setToast]=useState(null); const [busy,setBusy]=useState(false);
const folderNames=Object.keys(favFolders.folders||{}); const [favViewFolder,setFavViewFolder]=useState(()=>folderNames[0]||'Unfiled');
const [favModal,setFavModal]=useState({open:false,target:null,newFolder:''}); const [mgrModal,setMgrModal]=useState({open:false,renameFrom:'',renameTo:'',delTarget:'',moveTarget:''}); const [delFavModal,setDelFavModal]=useState({open:false,target:null,all:false}); const [exportModal,setExportModal]=useState({open:false,password:''}); const [importModal,setImportModal]=useState({open:false,password:''});
const importTexturesRef=useRef(null), importFavRef=useRef(null), exportAttachRef=useRef(null);
const baseList=useMemo(()=>{if(view!=='favorites')return dataset; const keys=new Set((favFolders.folders[favViewFolder]||[])); return dataset.filter(t=>keys.has(favKeyOf(t)));},[view,dataset,favFolders,favViewFolder]);
const filtered=useMemo(()=>{const q=norm(query); let data=baseList.filter(t=>[t.textureName,t.txdName,t.modelId,t.libraryName].map(String).some(v=>norm(v).includes(q))); if(hideDupes) data=data.filter(t=>!t.duplicateOf); return data;},[baseList,query,hideDupes]);
const total=filtered.length, maxPage=Math.max(1,Math.ceil(total/pageSize)), pageSafe=Math.min(page,maxPage); const items=paginate(filtered,pageSize,pageSafe);
function showToast(kind,msg){setToast({type:kind,msg}); clearTimeout(showToast._t); showToast._t=setTimeout(()=>setToast(null),3000);}
const isFavAnywhere=t=>{const k=favKeyOf(t); return Object.values(favFolders.folders).some(l=>l.includes(k));};
function addFavToFolder(t,folder){const k=favKeyOf(t); setFavFolders(prev=>{const folders={...prev.folders}; const s=new Set(folders[folder]||[]); s.add(k); folders[folder]=Array.from(s); return {folders,lastFolder:folder};});}
function removeFavFromFolder(t,folder){const k=favKeyOf(t); setFavFolders(prev=>{const folders={...prev.folders}; folders[folder]=(folders[folder]||[]).filter(x=>x!==k); return {folders,lastFolder:prev.lastFolder};});}
function removeFavFromAll(t){const k=favKeyOf(t); setFavFolders(prev=>{const folders=Object.fromEntries(Object.entries(prev.folders).map(([f,arr])=>[f,arr.filter(x=>x!==k)])); return {folders,lastFolder:prev.lastFolder};});}
async function handleImportTextures(ev){const file=ev.target.files?.[0]; if(!file)return; setBusy(true); try{const text=await file.text(); const rows=parseTable(text); if(!rows.length) throw new Error('File kosong'); const header=rows[0].map(s=>String(s).trim().toLowerCase()); const dataRows=rows.slice(1); const col=names=>names.map(n=>String(n).toLowerCase()).map(n=>header.indexOf(n)).find(i=>i>=0); const idxModel=col(['modelid','model_id','model','id','model id']); const idxTXD=col(['txdname','txd','txd_name','txd name']); const idxName=col(['texturename','texture','name','texture name']); const idxURL=col(['url','image','img','link']); if(idxModel==null||idxTXD==null||idxName==null||idxURL==null) throw new Error('Header wajib: modelid, txdname, texturename, url'); const next=[]; let autoId=1; for(const r of dataRows){ if(r.every(c=>String(c).trim()==='')) continue; const modelIdRaw=r[idxModel]; const modelId=(modelIdRaw==null||String(modelIdRaw).trim()==='')?null:Number(modelIdRaw); const txdName=String(r[idxTXD]??'').trim(); const textureName=String(r[idxName]??'').trim(); let url=String(r[idxURL]??'').trim(); if(!url) url=`https://gtxd.net/images/gtasa_textures/${txdName}.${textureName}.png`; if(!modelId||!txdName||!textureName) continue; next.push({id:autoId++,textureName,modelId,txdName,libraryName:'gta3.img',duplicateOf:null,urlEnc:encUrl(url),tags:[]}); } if(!next.length) throw new Error('Tidak ada baris valid'); setDataset(next); setView('home'); setQuery(''); setHideDupes(false); setPage(1); showToast('success',`Berhasil memuat ${next.length} data.`);}catch(e){console.error(e); showToast('error',`Gagal import: ${e.message||e}`);} finally{setBusy(false); if(importTexturesRef.current) importTexturesRef.current.value='';}}
function clearImported(){setDataset([]); try{localStorage.removeItem(LS_KEY_DATA);}catch{} setPage(1); showToast('success','Dataset dikosongkan.');}
async function startExportFav(){setExportModal({open:true,password:''}); if(exportAttachRef.current) exportAttachRef.current.value='';}
async function confirmExportFav(){ if(!exportModal.password){showToast('error','Masukkan kunci/password.'); return;} setBusy(true); try{const out={}; for(const [folder,list] of Object.entries(favFolders.folders)){ out[folder]=list.map(k=>{const [modelId,txdName,textureName]=k.split('|'); const item=dataset.find(d=>d.modelId===Number(modelId)&&d.txdName===txdName&&d.textureName===textureName); const url=item?decUrl(item.urlEnc):`https://gtxd.net/images/gtasa_textures/${txdName}.${textureName}.png`; return {modelid:Number(modelId),txdname:txdName,texturename:textureName,url};}); } let attachment=null; const f=exportAttachRef.current?.files?.[0]; if(f){const raw=await f.arrayBuffer(); attachment={filename:f.name,mime:f.type||'application/octet-stream',data:toB64(raw)};} const packed=await encryptJSON(out,exportModal.password,attachment); const blob=new Blob([JSON.stringify(packed)],{type:'application/json'}); const url=URL.createObjectURL(blob); const a=document.createElement('a'); a.href=url; a.download=`texfind_favorites_${new Date().toISOString().slice(0,10)}.tfx.json`; document.body.appendChild(a); a.click(); a.remove(); URL.revokeObjectURL(url); setExportModal({open:false,password:''}); showToast('success','Favorites terenkripsi diexport.'); }catch(e){console.error(e); showToast('error',`Export gagal: ${e.message||e}`);} finally{setBusy(false); if(exportAttachRef.current) exportAttachRef.current.value='';}}
function startImportFav(){setImportModal({open:true,password:''}); if(importFavRef.current) importFavRef.current.value='';}
async function confirmImportFav(){const file=importFavRef.current?.files?.[0]; if(!file){showToast('error','Pilih file (.tfx.json).'); return;} if(!importModal.password){showToast('error','Masukkan kunci/password.'); return;} setBusy(true); try{const packed=JSON.parse(await file.text()); const payload=await decryptJSON(packed,importModal.password); const data=payload.data||{}; const folders={...favFolders.folders}; let added=0; for(const [folder,arr] of Object.entries(data)){ if(!Array.isArray(arr)) continue; if(!folders[folder]) folders[folder]=[]; const set=new Set(folders[folder]); for(const obj of arr){const k=`${obj.modelid}|${obj.txdname}|${obj.texturename}`; if(!set.has(k)){set.add(k); added++;}} folders[folder]=Array.from(set);} setFavFolders({folders,lastFolder:favFolders.lastFolder}); setImportModal({open:false,password:''}); showToast('success',`Favorites loaded: ${added} entri baru.`);}catch(e){console.error(e); showToast('error',`Import gagal: ${e.message||e}`);} finally{setBusy(false); if(importFavRef.current) importFavRef.current.value='';}}
function openMgr(){setMgrModal({open:true,renameFrom:'',renameTo:'',delTarget:'',moveTarget:''});} function closeMgr(){setMgrModal({open:false,renameFrom:'',renameTo:'',delTarget:'',moveTarget:''});}
function createFolder(){const name=prompt('Nama folder baru:'); if(!name) return; if(favFolders.folders[name]){showToast('error','Folder sudah ada.'); return;} setFavFolders(prev=>({folders:{...prev.folders,[name]:[]},lastFolder:name})); setFavViewFolder(name); showToast('success',`Folder "${name}" dibuat.`);}
function renameFolder(){const f=mgrModal.renameFrom,t=mgrModal.renameTo.trim(); if(!f||!t){showToast('error','Pilih folder & nama baru.'); return;} if(favFolders.folders[t]){showToast('error','Nama baru sudah ada.'); return;} setFavFolders(prev=>{const folders={...prev.folders}; folders[t]=folders[f]; delete folders[f]; return {folders,lastFolder:prev.lastFolder===f?t:prev.lastFolder};}); if(favViewFolder===f) setFavViewFolder(t); showToast('success',`Folder "${f}" → "${t}".`);}
function deleteFolder(){const d=mgrModal.delTarget; if(!d){showToast('error','Pilih folder.'); return;} if(!confirm(`Hapus folder "${d}"?`)) return; setFavFolders(prev=>{const folders={...prev.folders}; delete folders[d]; return {folders,lastFolder: prev.lastFolder===d?(Object.keys(folders)[0]||'Unfiled'):prev.lastFolder};}); if(favViewFolder===d) setFavViewFolder(Object.keys(favFolders.folders)[0]||'Unfiled'); showToast('success',`Folder "${d}" dihapus.`);}
function moveAll(){const s=mgrModal.renameFrom,d=mgrModal.moveTarget; if(!s||!d||s===d){showToast('error','Pilih asal & tujuan berbeda.'); return;} setFavFolders(prev=>{const folders={...prev.folders}; const set=new Set([...(folders[d]||[]),...(folders[s]||[])]); folders[d]=Array.from(set); folders[s]=[]; return {folders,lastFolder:prev.lastFolder};}); showToast('success',`Semua item dipindah: ${s} → ${d}.`);}
function openDelFav(t){setDelFavModal({open:true,target:t,all:false});} function confirmDelFav(){const t=delFavModal.target; if(!t)return; if(delFavModal.all) removeFavFromAll(t); else removeFavFromFolder(t,favViewFolder); setDelFavModal({open:false,target:null,all:false}); showToast('success','Favorite dihapus.');}
return (<div className={`min-h-screen ${theme.app}`}>
{toast&&(<div className={`fixed top-4 right-4 z-50 px-4 py-2 rounded-xl border shadow ${toast.type==='success'?theme.success:theme.error}`}>{toast.msg}</div>)}
<header className={`sticky top-0 z-20 ${theme.header}`}><div className='max-w-6xl mx-auto px-4 py-4 flex items-center justify-between'><div className='text-2xl font-black tracking-tight'>TexFind</div><nav className='flex items-center gap-2'><button onClick={()=>setView('home')} className={`px-3 py-1 rounded-xl border text-sm ${view==='home'?theme.navHome:theme.button}`}>Home</button><button onClick={()=>setView('favorites')} className={`px-3 py-1 rounded-xl border text-sm ${view==='favorites'?theme.navFav:theme.button}`}>Favorites</button><button onClick={()=>setView('tutorial')} className={`px-3 py-1 rounded-xl border text-sm ${view==='tutorial'?theme.navTut:theme.button}`}>Tutorial</button></nav></div></header>
<main className='max-w-6xl mx-auto px-4 py-6'>
{view!=='tutorial' && (<div className='mb-4 flex flex-wrap items-center gap-2'>
<label className={`px-3 py-2 rounded-xl cursor-pointer ${theme.button}`}><input ref={importTexturesRef} type='file' accept='.csv,.tsv,.txt,text/csv,text/tab-separated-values,text/plain' className='hidden' onChange={handleImportTextures} disabled={busy}/>
Import Textures (CSV/TSV/TXT)</label>
<button onClick={clearImported} className={`px-3 py-2 rounded-xl ${theme.button}`} disabled={busy}>Clear data</button>
{view==='favorites' && (<><select className={`px-3 py-2 rounded-xl ${theme.select}`} value={favViewFolder} onChange={e=>setFavViewFolder(e.target.value)}>
{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}{!folderNames.includes('Unfiled')&&<option value='Unfiled'>Unfiled</option>}</select>
<button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={createFolder}>+ Folder</button>
<button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={openMgr}>Manage folders</button>
<button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={startExportFav} disabled={busy}>Export favorites (Encrypted)</button>
<button className={`px-3 py-2 rounded-xl ${theme.button}`} onClick={startImportFav} disabled={busy}>Load favorites (Encrypted)</button></>)}
<span className={`ml-auto text-xs ${theme.muted}`}>Data source: {dataset.length?'imported':'empty'} • {dataset.length} rows</span></div>)}
{view!=='tutorial' && dataset.length>0 && (<div className='mb-4 flex flex-wrap items-center gap-3'>
<input value={query} onChange={e=>{setPage(1); setQuery(e.target.value);}} placeholder={view==='favorites'?'Search favorites...':'Search textures...'} className={`w-full md:w-72 px-3 py-2 rounded-xl outline-none focus:ring-2 focus:ring-indigo-500 ${theme.input}`}/>
<div className='flex items-center gap-2'><label className={`text-sm ${theme.muted}`}>Show</label><select value={pageSize} onChange={e=>{setPage(1); setPageSize(parseInt(e.target.value,10));}} className={`px-3 py-2 rounded-xl ${theme.select}`}>{[20,40,60,80,100].map(n=><option key={n} value={n}>{n}</option>)}</select><span className={`text-sm ${theme.muted}`}>entries</span></div>
<label className='inline-flex items-center gap-2 text-sm'><input type='checkbox' checked={hideDupes} onChange={e=>{setPage(1); setHideDupes(e.target.checked);}}/>Hide duplicates</label>
<div className={`ml-auto text-sm ${theme.muted}`}>{total.toLocaleString()} item ditemukan</div></div>)}
{view==='tutorial' ? (<Tutorial/>) : items.length===0 ? (<div className={`rounded-2xl p-8 text-center ${theme.card}`}><div className='text-sm opacity-80 mb-2'>{dataset.length===0?'Belum ada data. Import file dulu.':'Tidak ada hasil yang cocok.'}</div><div className='text-xs opacity-60'>Coba hapus filter/pencarian.</div></div>) : (
<div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3'>{items.map(t=>(<div key={t.id} className='relative'>
<button onClick={()=>setActive(t)} className={`w-full text-left rounded-2xl p-4 hover:border-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 ${theme.card}`}>
<div className='text-sm font-semibold truncate pr-8'>{t.textureName}</div>
<div className={`mt-2 h-28 w-full rounded-xl flex items-center justify-center overflow-hidden ${theme.preview}`}><img src={decUrl(t.urlEnc)} alt={t.textureName} onError={e=>{e.currentTarget.style.display='none'; e.currentTarget.parentElement.innerText='No preview';}} className='w-full h-full object-cover'/></div>
<div className={`mt-2 text-xs ${theme.muted}`}>More info</div></button>
{view==='favorites' ? (<div className='absolute top-2 right-2 flex gap-2'><button onClick={e=>{e.stopPropagation(); openDelFav(t);}} className={`rounded-full border px-2 py-1 text-xs ${theme.button}`} title='Delete from favorites'>🗑</button></div>) : (<button aria-label={isFavAnywhere(t)?'Remove from favorites':'Add to favorites'} onClick={e=>{e.stopPropagation(); if(isFavAnywhere(t)){openDelFav(t);} else {setFavModal({open:true,target:t,newFolder:''});}}} className={`absolute top-2 right-2 rounded-full border px-2 py-1 text-xs backdrop-blur transition ${isFavAnywhere(t)?theme.favActive:theme.favIdle}`} title={isFavAnywhere(t)?'Favorited':'Add to favorites'}><HeartIcon filled={isFavAnywhere(t)}/></button>)}
</div>))}</div>)}
</main>
{active && (<div className='fixed inset-0 z-30 bg-black/70 backdrop-blur-sm flex items-center justify-center p-4' onClick={()=>setActive(null)}><div className={`w-full max-w-2xl rounded-2xl ${theme.modal}`} onClick={e=>e.stopPropagation()}>
<div className='p-4 border-b border-inherit flex items-start justify-between'><div><div className='text-lg font-bold flex items-center gap-2'>{active.textureName}</div><div className={`text-xs ${theme.muted}`}>Texture details</div></div><button onClick={()=>setActive(null)} className={`px-3 py-1 rounded-xl ${theme.button}`}>Close</button></div>
<div className='p-4 grid grid-cols-1 md:grid-cols-2 gap-4'><div className='space-y-3'><div className={`h-40 rounded-xl flex items-center justify-center overflow-hidden ${theme.preview}`}><img src={decUrl(active.urlEnc)} alt={active.textureName} onError={e=>{e.currentTarget.style.display='none'; e.currentTarget.parentElement.innerText='No preview';}} className='w-full h-full object-cover'/></div></div><div className='space-y-2 text-sm'><Row label='Model ID' value={active.modelId??'-'}/><Row label='TXD Name' value={active.txdName}/><Row label='Texture Name' value={active.textureName}/></div></div>
<div className='px-4 pb-4'><div className={`rounded-xl p-3 text-xs space-y-2 ${theme.card}`}><div className='font-semibold'>In-Game Commands</div><Code label='/edittexture' code={`/edittexture ${active.modelId??''} ${active.txdName} ${active.textureName}`}/><Code label='/mmat' code={`/mmat ${active.modelId??''} ${active.txdName} ${active.textureName}`}/><Code label='Builder Code' code={`${active.modelId??''} ${active.txdName} ${active.textureName}`}/></div></div>
</div></div>)}
{favModal.open && (<div className='fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4' onClick={()=>setFavModal({open:false,target:null,newFolder:''})}><div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={e=>e.stopPropagation()}><div className='text-lg font-bold mb-2'>Simpan ke folder</div><label className='text-sm opacity-80'>Pilih folder</label><select className={`mt-1 w-full px-3 py-2 rounded-xl ${theme.select}`} defaultValue={favFolders.lastFolder||(folderNames[0]||'Unfiled')} onChange={e=>setFavFolders(prev=>({...prev,lastFolder:e.target.value}))}>{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}{!folderNames.includes('Unfiled')&&<option value='Unfiled'>Unfiled</option>}</select><div className='mt-3 text-sm opacity-80'>…atau buat folder baru</div><input className={`mt-1 w-full px-3 py-2 rounded-xl ${theme.input}`} placeholder='Nama folder baru (opsional)' value={favModal.newFolder} onChange={e=>setFavModal(m=>({...m,newFolder:e.target.value}))}/><div className='mt-4 flex gap-2 justify-end'><button onClick={()=>setFavModal({open:false,target:null,newFolder:''})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Batal</button><button onClick={()=>{const folder=(favModal.newFolder||favFolders.lastFolder||'Unfiled').trim(); if(!folder)return; if(!favFolders.folders[folder]) setFavFolders(prev=>({folders:{...prev.folders,[folder]:[]},lastFolder:folder})); addFavToFolder(favModal.target,folder); setFavModal({open:false,target:null,newFolder:''}); showToast('success',`Disimpan ke folder "${folder}".`);}} className={`px-3 py-2 rounded-xl ${theme.button}`}>Simpan</button></div></div></div>)}
{delFavModal.open && (<div className='fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4' onClick={()=>setDelFavModal({open:false,target:null,all:false})}><div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={e=>e.stopPropagation()}><div className='text-lg font-bold mb-2'>Hapus dari favorites?</div><label className='inline-flex items-center gap-2 text-sm'><input type='checkbox' checked={delFavModal.all} onChange={e=>setDelFavModal(m=>({...m,all:e.target.checked}))}/>Hapus dari semua folder</label><div className='mt-4 flex gap-2 justify-end'><button onClick={()=>setDelFavModal({open:false,target:null,all:false})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Batal</button><button onClick={confirmDelFav} className={`px-3 py-2 rounded-xl ${theme.button}`}>Hapus</button></div></div></div>)}
{mgrModal.open && (<div className='fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4' onClick={closeMgr}><div className={`w-full max-w-lg rounded-2xl ${theme.modal} p-4`} onClick={e=>e.stopPropagation()}><div className='text-lg font-bold mb-4'>Manage folders</div><div className='grid md:grid-cols-2 gap-4'>
<div className={`rounded-xl p-3 ${theme.card}`}><div className='font-semibold mb-2'>Rename</div><select className={`w-full px-3 py-2 rounded-xl ${theme.select}`} value={mgrModal.renameFrom} onChange={e=>setMgrModal(m=>({...m,renameFrom:e.target.value}))}><option value=''>-- pilih folder --</option>{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}</select><input className={`w-full px-3 py-2 rounded-xl mt-2 ${theme.input}`} placeholder='Nama baru' value={mgrModal.renameTo} onChange={e=>setMgrModal(m=>({...m,renameTo:e.target.value}))}/><button onClick={renameFolder} className={`mt-2 px-3 py-2 rounded-xl ${theme.button}`}>Rename</button></div>
<div className={`rounded-xl p-3 ${theme.card}`}><div className='font-semibold mb-2'>Delete</div><select className={`w-full px-3 py-2 rounded-xl ${theme.select}`} value={mgrModal.delTarget} onChange={e=>setMgrModal(m=>({...m,delTarget:e.target.value}))}><option value=''>-- pilih folder --</option>{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}</select><button onClick={deleteFolder} className={`mt-2 px-3 py-2 rounded-xl ${theme.button}`}>Delete</button></div>
<div className={`rounded-xl p-3 ${theme.card}`}><div className='font-semibold mb-2'>Move all</div><div className='flex gap-2'><select className={`w-full px-3 py-2 rounded-xl ${theme.select}`} value={mgrModal.renameFrom} onChange={e=>setMgrModal(m=>({...m,renameFrom:e.target.value}))}><option value=''>Dari…</option>{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}</select><select className={`w-full px-3 py-2 rounded-xl ${theme.select}`} value={mgrModal.moveTarget} onChange={e=>setMgrModal(m=>({...m,moveTarget:e.target.value}))}><option value=''>Ke…</option>{folderNames.map(f=>(<option key={f} value={f}>{f}</option>))}</select></div><button onClick={moveAll} className={`mt-2 px-3 py-2 rounded-xl ${theme.button}`}>Move</button></div>
<div className={`rounded-xl p-3 ${theme.card}`}><div className='font-semibold mb-2'>Create</div><button onClick={createFolder} className={`px-3 py-2 rounded-xl ${theme.button}`}>+ Folder</button></div>
</div><div className='mt-4 flex justify-end'><button onClick={closeMgr} className={`px-3 py-2 rounded-xl ${theme.button}`}>Close</button></div></div></div>)}
{exportModal.open && (<div className='fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4' onClick={()=>setExportModal({open:false,password:''})}><div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={e=>e.stopPropagation()}><div className='text-lg font-bold mb-2'>Export favorites (Encrypted)</div><input type='password' value={exportModal.password} onChange={e=>setExportModal(m=>({...m,password:e.target.value}))} placeholder='Masukkan kunci/password' className={`w-full px-3 py-2 rounded-xl ${theme.input}`}/><div className='text-[11px] mt-1 opacity-70'>Catatan: <b>jangan sampai lupa</b> kunci/password ini. Tanpa kunci, file tidak bisa dibuka.</div><div className='mt-3 text-sm'>Attachment (opsional):</div><input ref={exportAttachRef} type='file' className='w-full text-xs mt-1'/><div className='mt-4 flex gap-2 justify-end'><button onClick={()=>setExportModal({open:false,password:''})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Batal</button><button onClick={confirmExportFav} className={`px-3 py-2 rounded-xl ${theme.button}`} disabled={busy}>Export</button></div></div></div>)}
{importModal.open && (<div className='fixed inset-0 z-40 bg-black/70 flex items-center justify-center p-4' onClick={()=>setImportModal({open:false,password:''})}><div className={`w-full max-w-sm rounded-2xl ${theme.modal} p-4`} onClick={e=>e.stopPropagation()}><div className='text-lg font-bold mb-2'>Load favorites (Encrypted)</div><input type='password' value={importModal.password} onChange={e=>setImportModal(m=>({...m,password:e.target.value}))} placeholder='Masukkan kunci/password' className={`w-full px-3 py-2 rounded-xl ${theme.input}`}/><div className='text-[11px] mt-1 opacity-70'>Catatan: <b>jangan lupa</b> kunci/password yang dipakai ketika export.</div><input ref={importFavRef} type='file' accept='application/json,.json' className='w-full text-xs mt-3'/><div className='mt-4 flex gap-2 justify-end'><button onClick={()=>setImportModal({open:false,password:''})} className={`px-3 py-2 rounded-xl ${theme.button}`}>Batal</button><button onClick={confirmImportFav} className={`px-3 py-2 rounded-xl ${theme.button}`} disabled={busy}>Load</button></div></div></div>)}
<footer className='max-w-6xl mx-auto px-4 py-10 opacity-70 text-xs'><div>TexFind • rezam5n / dvberg • © 2025</div></footer>
</div>);}
